LATEST_POSTS_NUM = 3

# Create your views here.

# get three latest posts and send back some welcome text along with 3 latest posts


def index(request):
    try:
        latest_posts = []
        pq = []
        heapq.heapify(pq)
        
        # get latest LATEST_POSTS_NUM posts
        for post in dummy_posts:
            if len(pq) < LATEST_POSTS_NUM:
                heapq.heappush(pq, (post["time"], post))
            else:
                if pq[0][0] < post["time"]:
                    heapq.heappop(pq)
                    heapq.heappush(pq, (post["time"], post))

        for _, post in pq:
            latest_posts.append(post)
            
        return render(request,"blog/index.html",{
          "posts":latest_posts
        })
    except:
        return HttpResponseServerError("Something Went Wrong!!")


def posts(request):
    try:
      all_posts = dummy_posts 
      return render(request,"blog/posts.html",{
        "posts":all_posts
      })
    except:
      return HttpResponseServerError("Something Went Wrong!!")


def single_post(request, id):
  try:
    found_post = None
    for post in dummy_posts:
      if post["id"] == int(id):
        found_post = post
        break
    
    if found_post == None:
      raise Exception()

    return render(request,"blog/post.html",{
      "post":found_post
    })
  except:
    raise Http404()
